import React, { useState, useEffect } from 'react';
import { PlayButton, PrevButton, NextButton, Progress, Timer, VolumeControl } from 'react-soundplayer/components';

import './jamendo.css'; // Импортируем созданные стили

const JamendoPlayer = () => {
  const [tracks, setTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://api.jamendo.com/v3.0/tracks/?client_id=YOUR_JAMENDO_API_KEY`
        );
        const data = await response.json();
        setTracks(data.results);
      } catch (error) {
        console.error('Ошибка получения данных:', error);
      }
    };

    fetchData();
  }, []);

  const playTrack = () => {
    setIsPlaying(true);
  };

  const pauseTrack = () => {
    setIsPlaying(false);
  };

  const nextTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
  };

  const previousTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + tracks.length) % tracks.length);
  };

  const handleVolumeChange = (e) => {
    setVolume(e.target.value);
  };

  return (
    <div className="player-container">
      <div>
        <PrevButton onPrev={previousTrack} />
        <PlayButton
          className={isPlaying ? 'pause' : 'play'}
          onPlay={playTrack}
          onPause={pauseTrack}
          playing={isPlaying}
        />
        <NextButton onNext={nextTrack} />
      </div>
      <div className="audio-player">
        <h4 className="artist-title">{tracks[currentTrackIndex]?.artist_name} - {tracks[currentTrackIndex]?.name}</h4>
        <div className="progress-bar">
          <Progress
            duration={tracks[currentTrackIndex]?.duration}
            currentTime={0}
            bufferedTime={0}
          />
        </div>
        <div className="volume-control">
          <VolumeControl
            volume={volume}
            onVolumeChange={handleVolumeChange}
          />
        </div>
      </div>
    </div>
  );
};

export default JamendoPlayer;
