import React, { Component } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const API_KEY = 'GXeqDKaA5Wl9wGsKVlzwffueCV5vU7zYUeONkxUVdsw'; // замените на ваш ключ API

export default class CarouselBox extends Component {
  state = {
    images: []
  }

  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/random?count=5&client_id=${API_KEY}`)
      .then(res => res.json())
      .then(data => {
        this.setState({ images: data })
      })
      .catch(err => console.error(err));
  }

  render() {
    const { images } = this.state;

    return (
      <Carousel autoPlay infiniteLoop useKeyboardArrows dynamicHeight>
        {images.map((image, index) => (
          <div key={image.id}>
            <img
              src={image.urls.regular}
              alt={image.alt_description}
            />
            <p className="legend">Photo by {image.user.name} on Unsplash</p>
          </div>
        ))}
      </Carousel>
    )
  }
}
