import React, { Component } from 'react';

const API_KEY = 'YOUR_UNSPLASH_API_KEY'; // замените на ваш ключ API

export default class CarouselBox extends Component {
  state = {
    images: [],
    currentImageIndex: 0,
  }

  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/random?count=5&client_id=${GXeqDKaA5Wl9wGsKVlzwffueCV5vU7zYUeONkxUVdsw}`)
      .then(res => res.json())
      .then(data => {
        this.setState({ images: data })
      })
      .catch(err => console.error(err));

    this.carouselInterval = setInterval(() => {
      this.setState(prevState => ({
        currentImageIndex: (prevState.currentImageIndex + 1) % prevState.images.length,
      }));
    }, 3000);
  }

  componentWillUnmount() {
    clearInterval(this.carouselInterval);
  }

  render() {
    const { images, currentImageIndex } = this.state;
    if (images.length === 0) return null;

    const currentImage = images[currentImageIndex];

    return (
      <div style={{ position: 'relative', width: '100%', height: '100vh', overflow: 'hidden' }}>
        <img
          src={currentImage.urls.regular}
          alt={currentImage.alt_description}
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
        <div style={{ position: 'absolute', bottom: '10px', left: '10px', color: 'white' }}>
          <h3>{currentImage.alt_description}</h3>
          <p>Photo by {currentImage.user.name} on Unsplash</p>
        </div>
        <button
          onClick={() => this.setState({ currentImageIndex: (currentImageIndex - 1 + images.length) % images.length })}
          style={{ position: 'absolute', top: '50%', left: '0', transform: 'translateY(-50%)', width: '60px', height: '60px' }}
        >
          Prev
        </button>
        <button
          onClick={() => this.setState({ currentImageIndex: (currentImageIndex + 1) % images.length })}
          style={{ position: 'absolute', top: '50%', right: '0', transform: 'translateY(-50%)', width: '60px', height: '60px' }}
        >
          Next
        </button>
      </div>
    )
  }
}
