import React, { useState, useEffect, useRef } from 'react';
import { IconButton, Slider } from '@mui/material';
import { SkipPrevious, PlayArrow, Pause, SkipNext } from '@mui/icons-material';

import './jamendo.css';

const JamendoPlayer = () => {
  const [tracks, setTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(50);
  const audioRef = useRef(new Audio());

  useEffect(() => {
    const fetchData = () => {
      const xhr = new XMLHttpRequest();
      xhr.open(
        'GET',
        'https://api.jamendo.com/v3.0/tracks/?client_id=e93b5922',
        true
      );

      xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
          const response = JSON.parse(xhr.responseText);
          const tracks = response.results;
          setTracks(tracks);
          console.log(tracks);
        } else if (xhr.readyState === 4) {
          console.error('Ошибка получения данных:', xhr.status);
        }
      };

      xhr.send();
    };

    fetchData();
  }, []);

  const playTrack = (audioUrl) => {
    setIsPlaying(true);
    audioRef.current.src = audioUrl;
    audioRef.current.play();
  };

  const pauseTrack = () => {
    setIsPlaying(false);
    audioRef.current.pause();
  };

  const nextTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
    audioRef.current.pause();
  };

  const previousTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + tracks.length) % tracks.length);
    audioRef.current.pause();
  };

  const handleVolumeChange = (event, newValue) => {
    const normalizedValue = newValue / 100; // Приводим значение к диапазону [0, 1]
    setVolume(newValue);
    audioRef.current.volume = normalizedValue;
  };

  return (
    <div className="player-container">
      <div className="player-controls">
        <IconButton onClick={previousTrack}>
          <SkipPrevious />
        </IconButton>
        {isPlaying ? (
          <IconButton onClick={pauseTrack}>
            <Pause />
          </IconButton>
        ) : (
          <IconButton onClick={() => playTrack(tracks[currentTrackIndex]?.audio)}>
            <PlayArrow />
          </IconButton>
        )}
        <IconButton onClick={nextTrack}>
          <SkipNext />
        </IconButton>
      </div>
      <div className="track-info">
        <h4>{tracks[currentTrackIndex]?.artist_name} - {tracks[currentTrackIndex]?.name}</h4>
      </div>
      <div className="progress-bar">
        <Slider value={0} min={0} max={100} />
      </div>
      <div className="volume-control">
        <Slider value={volume} onChange={handleVolumeChange} />
      </div>
    </div>
  );
};

export default JamendoPlayer;
