import styled from 'styled-components';

export const DotButton = styled.button`
  font-size: 1.2rem;
  border: none;
  background-color: transparent;
  padding: 0.2rem;
  margin: 0 0.5rem;
  cursor: pointer;
`;

export const PrevButton = styled.button`
  font-size: 2rem;
  border: none;
  background-color: transparent;
  position: absolute;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  cursor: pointer;
`;

export const NextButton = styled.button`
  font-size: 2rem;
  border: none;
  background-color: transparent;
  position: absolute;
  top: 50%;
  right: 0;
  transform: translateY(-50%);
  cursor: pointer;
`;
