import React, { useState, useEffect } from 'react';

const JamendoPlayer = () => {
  const [tracks, setTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const fetchData = () => {
      const xhr = new XMLHttpRequest();
      xhr.open(
        'GET',
        `https://api.jamendo.com/v3.0/tracks/?client_id=e93b5922`,
        true
      );

      xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
          const response = JSON.parse(xhr.responseText);
          const data = response.results;
          setTracks(data);
        } else if (xhr.readyState === 4) {
          console.error('Ошибка получения данных:', xhr.status);
        }
      };

      xhr.send();
    };

    fetchData();
  }, []);

  const playTrack = () => {
    setIsPlaying(true);
  };

  const pauseTrack = () => {
    setIsPlaying(false);
  };

  const nextTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
  };

  const previousTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + tracks.length) % tracks.length);
  };

  return (
    <div>
      <h1>Jamendo Player</h1>
      {tracks.length > 0 ? (
        <div>
          <h2>{tracks[currentTrackIndex].name}</h2>
          <audio
            src={tracks[currentTrackIndex].audio}
            controls
            autoPlay={isPlaying}
          />
          <button onClick={isPlaying ? pauseTrack : playTrack}>
            {isPlaying ? 'Pause' : 'Play'}
          </button>
          <button onClick={previousTrack}>Previous</button>
          <button onClick={nextTrack}>Next</button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default JamendoPlayer;
