import React, { useEffect, useState } from 'react';
import { Carousel } from 'react-bootstrap';


const API_KEY = 'GXeqDKaA5Wl9wGsKVlzwffueCV5vU7zYUeONkxUVdsw';
const API_URL = 'https://api.unsplash.com/photos/random?count=5';

const UnsplashCarousel = () => {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await fetch(API_URL, {
          headers: {
            Authorization: `Client-ID ${API_KEY}`
          }
        });

        if (!response.ok) {
          throw new Error('Request failed');
        }

        const data = await response.json();
        setPhotos(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchPhotos();
  }, []);

  return (
    <div>
      <Carousel>
        {photos.map((photo) => (
          <Carousel.Item key={photo.id}>
            <img className="d-block w-100" src={photo.urls.regular} alt={photo.alt_description} />
            <Carousel.Caption>
              <h3>{photo.description}</h3>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>
    </div>
  );
};

export default UnsplashCarousel;
