import React, { useRef, useEffect } from 'react';
import { EmblaCarousel } from 'embla-carousel/react';
import { DotButton, PrevButton, NextButton } from './EmblaCarouselStyles'; // импортируйте стили

const API_KEY = 'GXeqDKaA5Wl9wGsKVlzwffueCV5vU7zYUeONkxUVdsw'; // замените на ваш ключ API

const Carousel = () => {
  const carouselRef = useRef(null);
  const options = {
    loop: true,
    dragFree: true,
  };

  useEffect(() => {
    // Инициализация Embla Carousel после монтирования компонента
    if (carouselRef.current) {
      EmblaCarousel(carouselRef.current, options);
    }
  }, []);

  const fetchImages = async () => {
    try {
      const response = await fetch(
        `https://api.unsplash.com/photos/random?count=5&client_id=${API_KEY}`
      );
      const data = await response.json();
      return data;
    } catch (error) {
      console.error(error);
    }
  };

  const renderSlides = () => {
    const images = fetchImages();

    return images.map((image) => (
      <div key={image.id}>
        <img src={image.urls.regular} alt={image.alt_description} />
      </div>
    ));
  };

  return (
    <div className="carousel">
      <EmblaCarousel htmlTagName="div" emblaRef={carouselRef}>
        {renderSlides()}
      </EmblaCarousel>

      <PrevButton onClick={() => carouselRef.current.scrollPrev()}>&#10094;</PrevButton>
      <NextButton onClick={() => carouselRef.current.scrollNext()}>&#10095;</NextButton>

      <DotButton onClick={() => carouselRef.current.scrollTo(0)}>1</DotButton>
      <DotButton onClick={() => carouselRef.current.scrollTo(1)}>2</DotButton>
      <DotButton onClick={() => carouselRef.current.scrollTo(2)}>3</DotButton>
      <DotButton onClick={() => carouselRef.current.scrollTo(3)}>4</DotButton>
      <DotButton onClick={() => carouselRef.current.scrollTo(4)}>5</DotButton>
    </div>
  );
};

export default Carousel;
