import React, { useState, useEffect } from 'react';
import { withSoundCloudAudio } from 'react-soundplayer/addons';
import { PlayButton, PrevButton, NextButton, Progress, Timer } from 'react-soundplayer/components';

const JamendoPlayer = ({ track, currentTime }) => {
  const [tracks, setTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);

  useEffect(() => {
    const fetchData = () => {
      const xhr = new XMLHttpRequest();
      xhr.open(
        'GET',
        `https://api.jamendo.com/v3.0/tracks/?client_id=e93b5922`,
        true
      );

      xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
          const response = JSON.parse(xhr.responseText);
          setTracks(response.results);
        } else if (xhr.readyState === 4) {
          console.error('Ошибка получения данных:', xhr.status);
        }
      };

      xhr.send();
    };

    fetchData();
  }, []);

  const playTrack = (index) => {
    setCurrentTrackIndex(index);
  };

  const nextTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
  };

  const previousTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + tracks.length) % tracks.length);
  };

  return (
    <div style={{ height: '64px' }}>
      <h1>Jamendo Player</h1>
      {tracks.length > 0 ? (
        <div>
          <h2>{tracks[currentTrackIndex].name}</h2>
          <PlayButton
            {...track}
            streamUrl={tracks[currentTrackIndex].audio}
            onPlay={() => playTrack(currentTrackIndex)}
          />
          <PrevButton onPrev={previousTrack} />
          <NextButton onNext={nextTrack} />
          <Progress {...track} />
          <Timer {...track} currentTime={currentTime} />
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default withSoundCloudAudio(JamendoPlayer);
