import React, { useEffect, useState } from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

const API_KEY = 'GXeqDKaA5Wl9wGsKVlzwffueCV5vU7zYUeONkxUVdsw';
const API_URL = 'https://api.unsplash.com/photos/random?count=5';

const UnsplashCarousel = () => {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await fetch(API_URL, {
          headers: {
            Authorization: `Client-ID ${API_KEY}`
          }
        });

        if (!response.ok) {
          throw new Error('Request failed');
        }

        const data = await response.json();
        setPhotos(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchPhotos();
  }, []);

  return (
    <Carousel>
      {photos.map((photo) => (
        <div key={photo.id}>
          <img
            src={photo.urls.regular}
            alt={photo.alt_description}
            style={{ width: '500px', height: '300px' }} // Здесь задайте нужные вам размеры
          />
          <p>{photo.description}</p>
        </div>
      ))}
    </Carousel>
  );
};

export default UnsplashCarousel;
