import React, { useState, useEffect } from 'react';

const LastFMPlayer = () => {
  const [tracks, setTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `http://ws.audioscrobbler.com/2.0/?method=chart.getTopTracks&api_key=81bec00215de8663c72722c57c1829fa&format=json`
        );
        const data = await response.json();
        const tracks = data.tracks.track;
        setTracks(tracks);
      } catch (error) {
        console.error('Ошибка получения данных:', error);
      }
    };

    fetchData();
  }, []);

  const playTrack = () => {
    setIsPlaying(true);
  };

  const pauseTrack = () => {
    setIsPlaying(false);
  };

  const nextTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
  };

  const previousTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + tracks.length) % tracks.length);
  };

  return (
    <div>
      <h1>Last.fm Player</h1>
      {tracks.length > 0 ? (
        <div>
          <h2>{tracks[currentTrackIndex].name}</h2>
          <audio
            src={tracks[currentTrackIndex].url}
            controls
            autoPlay={isPlaying}
          />
          <button onClick={isPlaying ? pauseTrack : playTrack}>
            {isPlaying ? 'Pause' : 'Play'}
          </button>
          <button onClick={previousTrack}>Previous</button>
          <button onClick={nextTrack}>Next</button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default LastFMPlayer;
