import React, { Component } from 'react';
import Carousel from 'react-bootstrap/Carousel';

const API_KEY = 'YOUR_UNSPLASH_API_KEY'; // замените на ваш ключ API

export default class CarouselBox extends Component {
  state = {
    images: []
  }

  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/random?count=5&client_id=${API_KEY}`)
      .then(res => res.json())
      .then(data => {
        this.setState({ images: data })
      })
      .catch(err => console.error(err));
  }

  render() {
    const { images } = this.state;

    return (
      <Carousel controls={true} indicators={true} pause={false}>
        {images.map((image, index) => (
          <Carousel.Item key={image.id} interval={3000}>
            <img
              className="d-block w-100"
              src={image.urls.regular}
              alt={image.alt_description}
            />
            <Carousel.Caption>
              <h3>{image.alt_description}</h3>
              <p>Photo by {image.user.name} on Unsplash</p>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>
    )
  }
}
