import React, { useState, useEffect } from 'react';
import { PlayButton, PrevButton, NextButton, Progress, Timer, VolumeControl } from 'react-soundplayer/components';

const JamendoPlayer = () => {
  const [tracks, setTracks] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);

  useEffect(() => {
    const fetchData = () => {
      const xhr = new XMLHttpRequest();
      xhr.open(
        'GET',
        `https://api.jamendo.com/v3.0/tracks/?client_id=e93b5922`,
        true
      );

      xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
          const response = JSON.parse(xhr.responseText);
          setTracks(response.results);
        } else if (xhr.readyState === 4) {
          console.error('Ошибка получения данных:', xhr.status);
        }
      };

      xhr.send();
    };

    fetchData();
  }, []);

  const playTrack = () => {
    setIsPlaying(true);
  };

  const pauseTrack = () => {
    setIsPlaying(false);
  };

  const nextTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
  };

  const previousTrack = () => {
    setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + tracks.length) % tracks.length);
  };

  const handleVolumeChange = (e) => {
    setVolume(e.target.value);
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', height: '64px' }}>
      <div style={{ marginRight: '10px' }}>
        <PrevButton onPrev={previousTrack} />
        <PlayButton
          className={isPlaying ? 'pause' : 'play'}
          onPlay={playTrack}
          onPause={pauseTrack}
          playing={isPlaying}
        />
        <NextButton onNext={nextTrack} />
      </div>
      <div style={{ flex: 1, marginRight: '10px', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
        <h2>{tracks[currentTrackIndex]?.artist_name} - {tracks[currentTrackIndex]?.name}</h2>
      </div>
      <div style={{ width: '100px' }}>
        <VolumeControl
          volume={volume}
          onVolumeChange={handleVolumeChange}
        />
      </div>
      <audio src={tracks[currentTrackIndex]?.audio} controls autoPlay={isPlaying} volume={volume} />
      <Progress
        duration={tracks[currentTrackIndex]?.duration}
        currentTime={0}
        bufferedTime={0}
      />
      <Timer
        duration={tracks[currentTrackIndex]?.duration}
        currentTime={0}
        {...tracks[currentTrackIndex]}
      />
    </div>
  );
};

export default JamendoPlayer;
