import React, { useState } from 'react';
import Modal from './Modal';
import Button from '../UI/Button';
import './BtnModal.css';

const BtnModal = () => {
  const [isOpen, setIsOpen] = useState(false);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div>
      <Button className="open-button" onClick={openModal}>Нажми на меня! -_0</Button>
      <Modal isOpen={isOpen} closeModal={closeModal} videoUrl="https://www.youtube.com/embed/dQw4w9WgXcQ?autoplay=1" />
    </div>
  );
};

export default BtnModal;
