import React, { Component } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import 'bootstrap/dist/css/bootstrap.min.css';

const API_KEY = 'GXeqDKaA5Wl9wGsKVlzwffueCV5vU7zYUeONkxUVdsw'; // замените на ваш ключ API

export default class CarouselBox extends Component {
  state = {
    images: []
  }

  componentDidMount() {
    fetch(`https://api.unsplash.com/photos/random?count=5&client_id=${API_KEY}`)
      .then(res => res.json())
      .then(data => {
        this.setState({ images: data })
      })
      .catch(err => console.error(err));
  }

  render() {
    const { images } = this.state;

    return (
      <div>
        <Carousel>
          {images.map((image, index) => (
            <Carousel.Item key={image.id}>
              <img
                className="d-block w-100"
                src={image.urls.regular}
                alt={`Slide ${index+1}`}
              />
              <Carousel.Caption>
                <h3>Image {index + 1}</h3>
                <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Quidem, est?</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
        </Carousel>
      </div>
    )
  }
}
